import React, { useEffect, useState } from "react";
import PinDropIcon from '@material-ui/icons/PinDrop';


const App = () =>{

    const [city , setCity] = useState(null);
    const [search , setSearch] = useState('Gujrat');

   useEffect( () =>{
    const watherapi = async () => {
        const apidata = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=c83b44c63179d5f8b3d2f6d46b9573f2`);
        const war = await apidata.json();
        // console.log(war);
        setCity(war.main);
        // setCity(war.weather.main);
    };
    watherapi();
   },[search]);
   
      
    return(
        <>
        <div className='wather_app'>
               <input
               type='text'
               className='input'
               onChange={ (even)=>{
                setSearch(even.target.value);
               }}
               />
        {!city ? (
            <p>no data found</p>
        ) : ( 
            <div>
            <div className='info'>
                <h1 className='H1'>
                <PinDropIcon style={{
                    fontSize: '2rem',
                    marginRight: '5px'}}/>
                    {search}
                </h1>
                {/* <h2 className='H2'>{city.weather}</h2> */}
                <h4 className='H4'>Temp {city.temp} ℃</h4>
                <p>{city.temp_max}℃ | {city.temp_min} ℃</p>
            </div>
           </div>
        )}
          
        </div>
            
        </>
    );
};

export default App;
